var archivos_view = Backbone.View.extend({
    el: 'body',
    defaults: {
        content_page: "#content_page"
    },
    events:{
        "click .btnGuardarTipoArchivo":"f_nuevoArchivo"
    },
    initialize: function(e){
        try {
            $("#tablaCuestionarios").DataTable();
        }catch(exception){
            console.log(exception);
        }
    },
    f_nuevoArchivo:function(e){
        var _this = this;
        tipo = $("#txtArchivo").val();
        if(tipo != ""){
            _this.model.f_nuevoTipoArchivo(tipo).done( function(response){
                if(response.ok){
                    location.href = BASE_URL+'catalogo-general/tipo-archivos';
                }else{
                    swal("Imposible","Alta de nuevo tipo de archivo","error");
                }
            });

        }else{
            $("#txtArchivo").css({'border-left-width': '5px','border-left-color': 'red'});
        }
    }

});

$(document).ready(function () {
    archivos_view = new archivos_view({model: new archivos_model()});
    
});