var Postulaciones_view = Backbone.View.extend({
    el:'body',
    defaults:{

    },
    events:{
        'click .btnNueve':'f_agendarEntrevista'
    },
    f_agendarEntrevista:function(valor){

        horario = valor.target.innerText;

        var fecha = new Date();
        fecha = fecha.getFullYear() +'-'+ (fecha.getMonth() + 1) + '-'+fecha.getDate();
        
        datos = {
            fecha   :fecha,
            hora    :horario
        }

        this.model.agendarEntrevista(datos).done(function(response){
            console.log(response);
            if(response.ok){
                swal("Felicitaciones", response.mensaje, "success");
            }else{
                swal("Upps!!","No fue posible, agendar el horario de entrevista","error");
            }
        });


        // this.model.agendarEntrevista()
    }
});

$(document).ready(function(){
    //Referencia al modelo
    Postulaciones_view = new Postulaciones_view({model: new PostulacionesPanel_model()}); 
});