/*
 * Validar enter
 */
function f_validarEnter(e){
    li_tecla = e.keyCode || e.which;  
    return (li_tecla == 13);
}

/*
 * Agrega el cero a un n√∫mero, en el caso de las horas, minutos o segundos.
 */
function f_agregaCero(li_int) {
  if(li_int < 10) {
    li_int = "0" + li_int;
  }
  return li_int;
}