var VacantesCandidato_view = Backbone.View.extend({
    el: 'body',
    defaults: {
        content_page: "#content_page"
    }
    ,events:{
        "click .btn_nach_detalle": "f_detalleVacante",
        "click .btn_nach_continuar": "f_mostrarCuestionariosVacante",
        "click .btn_nach_docs": "f_mostrarCargarDocumentos"        
    },
    initialize: function(e){        
        try {
            var _this = this;
        }catch(exception){
            console.log(exception);
        }
    },
    f_mostrarCargarDocumentos: function(e){
        var _this = this;
        lo_currentItem = e.currentTarget;
        ls_hash = $(lo_currentItem).parent().parent().parent().data("hash");
        var la_dataCollection = {};
        
        la_dataCollection = {
            hash: ls_hash
        }
        
        _this.model.f_mostrarCargarDocumentosModel(la_dataCollection).done(function (response){
            if(response.return < 0){
                swal("¡Error!", "Ocurrió un error inesperado, favor de contactar a sistemas.", "error");
                return -1;
            }
            
            $(_this.defaults.content_page).html(response.contenido);
        });
        
        return 1;
    },
    f_mostrarCuestionariosVacante: function(e){
        var _this = this;
        lo_currentItem = e.currentTarget;
        ls_hash = $(lo_currentItem).parent().parent().parent().data("hash");
        var la_dataCollection = {};
        
        la_dataCollection = {
            hash: ls_hash
        }
        
        _this.model.f_mostrarCuestionariosVacanteModel(la_dataCollection).done(function (response){
            if(response.return < 0){
                swal("¡Error!", "Ocurrió un error inesperado, favor de contactar a sistemas.", "error");
                return -1;
            }
            
            $(_this.defaults.content_page).html(response.contenido);
        });
        
        return 1;
    },
    f_detalleVacante: function(e){
        var _this = this;
        lo_currentItem = e.currentTarget;
        ls_hash = $(lo_currentItem).parent().parent().parent().data("hash");
        
        var la_dataCollection = {};
        
        la_dataCollection = {
            hash: ls_hash
        }
        
        _this.model.f_detalleVacanteModel(la_dataCollection).done(function (response){
            if(response.return < 0){
                swal("¡Error!", "Ocurrió un error inesperado, favor de contactar a sistemas.", "error");
                return -1;
            }
            
            $(_this.defaults.content_page).html(response.contenido);
        });
        
        return 1;
    }
});

$(document).ready(function () {
    VacantesCandidato_view = new VacantesCandidato_view({model: new VacantesCandidato_model()});    
});